import { useState } from 'react';
import './App.css';

function App() {
  const [city,  setCity] = useState('');

  const getData = (event) =>{
    fetch(`https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=fd1f7d070a0f0bed4f37ac8b1b98bb33`)
    .then((res=>res.json()))
    .then((finalRes)=>{
      console.log(finalRes);
    })

    event.preventDefault();
    setCity('')
  }




  return (
    <div className="w-[100%] h-[100vh] bg-[#4aacb1]">
      <div className='max-w-[1320px] mx-auto'>
        <h1 className='text-[40px] font-bold text-white'>Simple Weather App</h1>

        <form onSubmit={getData}>
          <input type='text' value={city} onChange={(e) =>setCity(e.target.value)} className='w-[300px] h-[40px] pl-3' placeholder='Enter City' /><button className='bg-blue-800 border-black m-4 p-3 font-bold text-white'>Submit</button>
        </form>

        <div className='w-[400px] mx-auto bg-white shadow-lg mt-[40px] p-[25px]'>

          <h3 className='font-bold text-[30px]'>Jaipur <span className='bg-[yellow]'>IN</span></h3>
          <h2 className='font-bold text-[40px]'>9.332 0c</h2>
          <img src='http://openweathermap.org/img/w/50d.png' />
          <p>Fog</p>

        </div>

      </div>
    </div>
  );
}

export default App;
